import { createGlobalStyle } from "styled-components"

export default createGlobalStyle`
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }
    body {
        font-family: Arial;
        font-size: 14px;
        background-color: #f1f1f1;
    }
    a {
        text-decoration: none;
        padding: 10px;
        color:#ffff;
        background-color: #3b4cca;
    }
`
